import React, { useState } from "react";
import Basiclayout from "../layout/basiclayout";
import "./AddPeople.css"
import axios from "axios";

export default function AddPeople() {
  const [file, setFile] = useState("");

  function convertToBase64(e) {
    console.log(e);
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () => {
      setFile(reader.result);
    };
    reader.onerror = (error) => {
      console.log("Error reading", error);
    };
  }
  const url = "http://localhost:5000/users";
  const createImage = (newImage) => axios.post(url, newImage);
  const createPost = async (post) => {
    try {
      await createImage(post);
    } catch (error) {
      console.log(error.message);
    }
  };
  const nameHandler = (e) => {
    console.log(e.target.value);
  };

  return (
    <Basiclayout>
      <div className="form">
        <h1>Registration Form</h1>
          <div className="form-body">
              <div className="username">
                  <label className="form__label" for="firstName">Name</label>
                  <input className="form__input" type="text" id="firstName" placeholder="Enter your Name"/>
              </div>
              <div className="lastname">
                  <label className="form__label" for="lastName">Number of Leave</label>
                  <input  type="number" name="" id="lastName"  className="form__input"placeholder="Leave"/>
              </div>
              <div className="email">
                  <label className="form__label" for="email">Upload File</label>
                  <input  type="file" id="email" className="form__input"/>
              </div>


          </div>
          <div class="footer">
              <button type="submit" class="btn">Register</button>
          </div>
      </div>      
    </Basiclayout>
  );
}
