import { Fragment, useEffect, useState } from "react";
import axios from "axios";
import image from "../assents/notification-icons.png";
import Dashboard from "../Deshboard/Deshboard";
// import People from "../Sidebar/People";
import NewCard from "../UI/Card/NewCard";
import SideBar from "../Sidebar/sideBar";
import { SideNavLayout } from "../layout/side_nav_layout";
import Input from "../UI/Input/Input";
// import Input from "../UI/Input/Input";
// import classes from "../../components/Deshboard/Deshboard.module.css";

export default function People(props) {
  const [arr, setArr] = useState([1, 1, 1]);

  const [user, setUser] = useState([]);
  let [search, setSearch] = useState("");

  useEffect(async () => {
    const response = await axios.get(
      "https://rickandmortyapi.com/api/character"
    );
    console.log(response.data.results);
    setUser(response.data.results);
    // .then((response) =>{
    //     setUser(response.data);
    //     console.log(response.data.results.map(result => result.name));
    // });
  }, []);

  useEffect(() => {
    console.log(props);
  }, [props]);

  return (
    <Fragment>
      <SideNavLayout>
        {/* </SideNavLayout> */}
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div>
            <h1 className="sub_heading">{user.length} people</h1>
            <input
              onChange={(event) => {
                const result=user.filter((x) => {
                  search = search.toLowerCase
                  x.name.includes(search);
                })
                setSearch(event.target.value);
              }}
            />
          </div>
          <div className="cardList">
            {user
              .map((x) => {
                return (
                  <NewCard img={x?.image} name={x?.name} gender={x?.gender} />
                );
              })}
          </div>
        </div>
      </SideNavLayout>
    </Fragment>
  );
}
