import react from "react";
import { Fragment, useEffect, useState, useContext } from "react";
import axios from "axios";
import image from "../assents/notification-icons.png";
import Dashboard from "../Deshboard/Deshboard";
// import People from "../Sidebar/People";
import NewCard from "../UI/Card/NewCard";
import SideBar from "../Sidebar/sideBar";
import { SideNavLayout } from "../layout/side_nav_layout";
import { DataInfo } from "../GlobalContext/GlobalContext";
import Card from "../UI/Card/Card";
import Wrapper from "../UI/Wrapper/Wrapper";
// import Input from "../UI/Input/Input";

export default function People(props) {
  const { data } = useContext(DataInfo);

  const [UserData, setUserData] = data;
  // console.log(name);

  console.log(DataInfo);
  const [arr, setArr] = useState([1, 1, 1]);

  let [search, setSearch] = useState("");

  useEffect(() => {
    console.log("search value ", search);
  }, [search]);

  return (
    <div
    // style={{
    //   display: "flex",

    //   flexDirection: "column",
    //   justifyContent: "center",
    //   alignItems: "center",
    // }}>
    >
      <div>
        <h1 className="sub_heading">{UserData.length} people</h1>
        <input
          className="PeopleInput"
          onChange={(event) => {
            // const result=user.filter((x) => {
            //   search = search?.toLowerCase()
            //   console.log(search)
            //   x?.name.includes(search);
            // })
            // console.log(event.target.value)
            setSearch(event.target.value);
          }}
          placeholder="search Card"
        />
      </div>
      <Wrapper>
        {UserData?.filter((val, ind) => {
          // console.log('filter value ',val?.name?.toLowerCase())
          // if(){

          // }
          return val?.name?.toLowerCase()?.includes(search?.toLowerCase());
        })?.map((x) => {
          return <NewCard img={x?.image} name={x?.name} gender={x?.gender} />;
        })}
      </Wrapper>
    </div>
  );
}
