import React, { useState } from 'react'
import Input from '../UI/Input/Input'
import Button from '../UI/Button/Button'
import humanImage from '../assents/humanImage.png'


export default function FormLeave(props) {
  const[name, setName]=useState("");
  const[leave, setLeave]=useState();
  const[addPeople, setAddPeople]=useState("");
  
  const nameHandler = (event) => {
    setName(event.target.value);
    console.log(name);
  }

  const leaveHandler = (event) => {
    setLeave(event.target.value);
    console.log(leave)
  };

  const addPeopleHandler = (event) => {
    event.preventDefault();
    setAddPeople(event.target.value);
    console.log(addPeople)
  };

  const handleChange = (event) => {
    event.preventDefault();
    console.log(name, leave, addPeople);
  }
  return (

    <form className='Leave_Form' onSubmit={handleChange}>
      <h1>Leaving Registration Form</h1>
      <p className='sub-title'>Name</p>
     
      <input type="text" value={name} onChange={nameHandler} className="formInput"></input>
      <p className='sub-title'>Image</p>
      <br/>
      <input type="file"  className='file'></input>
      <p className='sub-title'>Total Leave</p>
      <input  type="number" value={leave} onChange={leaveHandler} className="formInput"></input>
      <p className='sub-title'>Add People</p>
      <input type="text" value={addPeople} onChange={addPeopleHandler} className="formInput"></input>
      <br></br>
      <Button onClick>Submit</Button>
    </form>
  )
}
