import React from "react";
import userImage from '../../assents/userImage.png'
export default function NewCard(props) {
  const[file, setFile]=useSt
  function convertToBase64(e){
    console.log(e)
    var reader = new FileReader();
    reader.readAsDataURL(e.target.files[0]);
    reader.onload = () => {
      setFile(reader.result);
    }
    reader.onerror = (error) => {
      console.log("Error reading", error);
    }
  }
  return (
    <div className="card">
      <img src={props.img} alt="hello" onChange={convertToBase64} />
      <h1 style={{color:"#fff"}}>{props.name}</h1>
      <h3 className="customer_designation" style={{color:"#fff"}}>Leave : {props.gender}</h3>

    </div>
  );
}
