/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { Fragment, useState } from "react";
import logo from "../../Images/logo.jpg";
import Tcs from "../../Images/Tcs.jpg"
import Dashboard from "../../Images/dashboard.svg";
import People from "../../Images/people.svg";
import AddPeople from "../../Images/AddPeople.svg";

import { useLocation,useNavigate } from "react-router-dom";

const SideBar2 = (props) => {
    const location = useLocation();
    const navigate = useNavigate();

    const [closeMenu, setCloseMenu] = useState(false);

    const handleCloseMenu = () => {
        setCloseMenu(!closeMenu);
    };

    return (
        <>
        <div className={closeMenu === false ? "sidebar" : "sidebar active"}>
            <div
                className={
                    closeMenu === false
                        ? "logoContainer"
                        : "logoContainer active"
                }
            >
                <img src={logo} alt="icon" className="logo" />
                <h2 className="title">Embifi</h2>
            </div>
            <div
                className={
                    closeMenu === false
                        ? "burgerContainer"
                        : "burgerContainer active"
                }
            >
                <div
                    className="burgerTrigger"
                    onClick={() => {
                        handleCloseMenu();
                    }}
                ></div>
                <div className="burgerMenu"></div>
            </div>
            <div
                className={
                    closeMenu === false
                        ? "profileContainer"
                        : "profileContainer active"
                }
            >
                <img src={Tcs} alt="profile" className="profile" />
                <div className="profileContents">
                    <p className="name">Hello, Sir👋</p>
                    <p>niyamul98@gmail.com</p>
                </div>
            </div>
            <div
                className={
                    closeMenu === false
                        ? "contentsContainer"
                        : "contentsContainer active"
                }
            >
                <ul>
                    <li className={location.pathname === "/" ? "active" : ""}>
                        <img src={Dashboard} alt="dashboard" onClick={()=>{navigate('/')}} />
                        <a onClick={()=>{navigate('/')}}>dashboard</a>
                       
                    </li>
                    <li
                        className={
                            location.pathname === "/People"
                                ? "active"
                                : ""
                        }
                    >
                        <img src={People} alt="transactions" />
                        <a onClick={()=>{
                            navigate('/people')
                        }}>People</a>
                    </li>
                    <li
                        className={
                            location.pathname === "/addPeople" ? "active" : ""
                        }
                    >
                        <img src={AddPeople} alt="Performance" />
                        <a onClick={() => {
                            navigate('/AddPeople')
                        }}>Add People</a>
                    </li>
                             </ul>
            </div>
        </div>
    
</>
    );
};

export default SideBar2;
