import React, { useState } from 'react'
import Input from '../UI/Input/Input'
import Button from '../UI/Button/Button'
import humanImage from '../assents/humanImage.png'
import classes from './FormLeave.module.css';


export default function FormLeave() {
  const[name, setName]=useState("");
  const[leave, setLeave]=useState();
  const[addPeople, setAddPeople]=useState("");
  
  const nameHandler = (event) => {
    setName(event.target.value);
    console.log(name);
  }

  const leaveHandler = (event) => {
    setLeave(event.target.value);
    console.log(leave)
  };

  const addPeopleHandler = (event) => {
    event.preventDefault();
    setAddPeople(event.target.value);
    console.log(addPeople)
  };

  const handleChange = (event) => {
    event.preventDefault();
  }
  return (

    <form className='Leave_Form' onSubmit={handleChange} className={classes.input}>
      <h1>Leaving Registration Form</h1>
      <p>Name</p>
      <br />
      <input type="name" onChange={nameHandler}></input>
      <p>Image</p>
      <br/>
      <input type="file" className='file'></input>
      <p>Total Leave</p>
      <input onChange={leaveHandler}></input>
      <p>Add People</p>
      <input type="name" onChange={addPeopleHandler}></input>
      <br></br>
      <Button>Submit</Button>
    </form>
  )
}
