import React, { useState } from "react";
import Input from "../UI/Input/Input";
import Button from "../UI/Button/Button";
import humanImage from "../assents/humanImage.png";
import { json } from "react-router-dom";

export default function FormLeave(props) {
  const [name, setName] = useState("");
  const[file,setFile]=useState('');
  const [leave, setLeave] = useState('');



  const nameHandler = (event) => {
    setName(event.target.value);
    console.log(name);
  };
  const fileHandle = (event) => {
    console.log(event);
    console.log(event.target.files[0]);
    setFile(event.target.files[0]);
  }

  const leaveHandler = (event) => {
    setLeave(event.target.value);
    console.log(leave);
  };


  const handleChange = (event) => {
    event.preventDefault();
   if(name == '' && leave == ''){
    return alert("entered input value");
   }
    setName('');
    setLeave('');
    console.log({name, leave});
    const data = {name, leave};
    fetch("https://userdataform-1cfb5-default-rtdb.firebaseio.com/userDataRecord.json", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body:JSON.stringify(data)
    }).then((result)=>{
       console.log("result");
    })
  };
  return (
    <div className="container">
    <form className="Leave_Form" onSubmit={handleChange}>
      <h1>Registration Form</h1>
      <label className="sub-title">Name</label>

      {/* <input
        type="text"
        value={name}
        onChange={nameHandler}
        // className="formInput"
      placeholder="entered your name" required ></input> */}
      <Input type="text"
      value={name}
      onChange={nameHandler}></Input>
      {/* <p className="sub-tietle">Image</p> */}
      <br />
      {/* <input type="file" className="file"  value={file[0]} onChange={fileHandle}></input> */}
      <Input type="file" value={file[0]} onChange={fileHandle}></Input>
      <label className="sub-title" required placeholder="enter your leave">Total Leave</label>
      {/* <input
        type="number"
        value={leave}
        onChange={leaveHandler}
        // className="formInput"
      ></input> */}
      <Input></Input>
      <br></br>
      <Button type="submit" onClick={handleChange}>Submit</Button>
    </form>
    </div>
  );
}
