import React, { useState } from "react";
import Input from "../UI/Input/Input";
import Button from "../UI/Button/Button";
import humanImage from "../assents/humanImage.png";
import { json } from "react-router-dom";

export default function FormLeave(props) {
  const [name, setName] = useState("");
  const [leave, setLeave] = useState('');


  const nameHandler = (event) => {
    setName(event.target.value);
    console.log(name);
  };

  const leaveHandler = (event) => {
    setLeave(event.target.value);
    console.log(leave);
  };


  const handleChange = (event) => {
    event.preventDefault();
   if(!name == '' && !leave == ''){
    return alert("entered input value");
   }
    setName('');
    setLeave('');
    console.log({name, leave});
    const data = {name, leave};
    fetch("https://userdataform-1cfb5-default-rtdb.firebaseio.com/userDataRecord.json", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body:JSON.stringify(data)
    }).then((result)=>{
       console.log("result");
    })
  };
  return (
    <form className="Leave_Form" onSubmit={handleChange}>
      <h1>Leaving Registration Form</h1>
      <p className="sub-title">Name</p>

      <input
        type="text"
        value={name}
        onChange={nameHandler}
        className="formInput"
      placeholder="entered your name" required ></input>
      <p className="sub-tietle">Image</p>
      <br />
      <input type="file" className="file"></input>
      <p className="sub-title" required >Total Leave</p>
      <input
        type="number"
        value={leave}
        onChange={leaveHandler}
        className="formInput"
      ></input>
      <Button type="submit" onClick={handleChange}>Submit</Button>
    </form>
  );
}
