  const [name, setName] = useState("");

  const [leave, setLeave] = useState("");
  const[file, setFile] = useState("");



  const nameHandler = (event) => {
    setName(event.target.value);
    console.log(name);
  };

  const leaveHandler = (event) => {
    setLeave(event.target.value);
    console.log(leave)
  };
  const url = "http://localhost:5000/users";
const createImage = (newImage) => axios.post(url, newImage);

  const createPost = async (post) => {
    try {
      await createImage(post);
    } catch (error) {
      console.log(error.message);
    }
  };

  const handleChange = (event) => {
    event.preventDefault();
  //  if(name == '' && leave == ''){
  //   return alert("entered input value");
  //  }


  return (
    <Basiclayout>
    <div className="container">
    <form className="Leave_Form" onSubmit={handleChange}>
      <h1>Registration Form</h1>
      <label className="sub-title">Name</label>

      {/* <input
        type="text"
        value={name}
        onChange={nameHandler}
        // className="formInput"
      placeholder="entered your name" required ></input> */}
      <input 
      type="text"
      value={name}
      onChange={nameHandler}
      ></input>
      {/* <p className="sub-tietle">Image</p> */}
      <br />
      {/* <input type="file" className="file"  value={file[0]} onChange={fileHandle}></input> */}

      <label className="sub-title" placeholder="enter your leave">Total Leave</label>
      {/* <input
        type="number"
        value={leave}
        onChange={leaveHandler}
        // className="formInput"
      ></input> */}
      <input type="number" value={leave} onChange={leaveHandler}></input>
      <br></br>
      <label className="sub-title">File</label>
      <input
          type="file"
          label="Image"
          name="myFile"
          accept=".jpeg, .png, .jpg"
          // onChange={handleFileUpload}
        />

      <br></br>
      <Button type="submit" onClick={handleChange}>Submit</Button>
    </form>
    </div>
    </Basiclayout>
  );
}
}
