/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState } from "react";
import SearchBar from "../../SearchBar/SearchBar";
import { useLocation, useNavigate } from "react-router-dom";
import logo from "../../Images/logo.jpg";
import Tcs from "../../Images/Tcs.jpg";
import dashboard from "../../Images/dashboard.svg";
import people from "../../Images/people.svg";
import AddPeople from "../../Images/AddPeople.svg";
import "./basiclayout.css";
import "../../styles/main.scss";

function Basiclayout(props) {
  const location = useLocation();
  const navigate = useNavigate();

  const [closeMenu, setCloseMenu] = useState(false);

  const handleCloseMenu = () => {
    setCloseMenu(!closeMenu);
  };

  return (
    // <div style={{display: "flex", height: "100%", justifyContent:"center"}}>
    //   <div style={{backgroundColor: "pink", width: "100vw", height: "5vh"}}>
    //     {/* <nav className='Nav-Bar'>

    //         <li className='link-list'>Dashboard</li>
    //         <li className='link-list'>People</li>
    //         <li className='link-list'>Add People</li>

    //     </nav> */}
    //     {/* <SideBar/> */}
    //   </div>
    //   <div style={{backgroundColor: "white", width: "100vw", height: "95vh"}}>
    //     <div style={{display:"flex", justifyContent:"space-between", alignItems:"center", width: "20vw", height: "100vh", backgroundColor:"#343336"}}>
    //      <h1 className="header">Hr</h1>
    //      <SearchBar/>

    //     </div>
    //     <div style={{width: "100%", height: "100%"}}>
    //         {props.children}
    //     </div>

    //   </div>

    // </div>
    <div style={{ backgroundColor: "pink" }} className="container1">
      <div className="item-1">
        <header>HR MANAGEMENT</header>
        <SearchBar />
      </div>
      <div style={{backgroundColor: "red"}}>
        <div className={closeMenu === false ? "sidebar" : "sidebar active"}>
          <div
            className={
              closeMenu === false ? "logoContainer" : "logoContainer active"
            }
          >
            <img src={logo} alt="icon" className="logo" />
            <h2 className="title">Embified</h2>
          </div>
          <div
            className={
              closeMenu === false ? "burgerContainer" : "burgerContainer active"
            }
          >
            <div
              className="burgerTrigger"
              onClick={() => {
                handleCloseMenu();
              }}
            ></div>
            <div className="burgerMenu"></div>
          </div>
          <div
            className={
              closeMenu === false
                ? "profileContainer"
                : "profileContainer active"
            }
          >
            <img src={Tcs} alt="profile" className="profile" />
            <div className="profileContents">
              <p className="name">Hello, SirðŸ‘‹</p>
              <p>niyamul98@gmail.com</p>
            </div>
          </div>
          <div
            className={
              closeMenu === false
                ? "contentsContainer"
                : "contentsContainer active"
            }
          >
            <ul>
              <li
                className={location.pathname === "/dashboard" ? "active" : ""}
              >
                <img src={dashboard} alt="dashboard" />
                <a
                  onClick={() => {
                    navigate("/dashboard");
                  }}
                >
                  dashboard
                </a>
              </li>
              <li className={location.pathname === "/People" ? "active" : ""}>
                <img src={people} alt="transactions" />
                <a
                  onClick={() => {
                    navigate("/people");
                  }}
                >
                  People
                </a>
              </li>
              <li
                className={location.pathname === "/addPeople" ? "active" : ""}
              >
                <img src={AddPeople} alt="Performance" />
                <a
                  onClick={() => {
                    navigate("/addPeople");
                  }}
                >
                  Add People
                </a>
              </li>
            </ul>
          </div>
        </div>
        <div className="item-3">{props.children} children</div>
      </div>
    </div>
  );
}

export default Basiclayout;
